}

function MovieDetails({ selectedId, onCloseMovie, onAddWatched, watched }) {
  const [movie, setMovie] = useState({});
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    async function fetchMovieDetails() {
      setIsLoading(true);
      try {
        const res = await fetch(
          `http://www.omdbapi.com/?apikey=${KEY}&i=${selectedId}`
        );
        const data = await res.json();
        setMovie(data);
      } catch (err) {
        console.error("Failed to fetch movie details:", err);
      } finally {
        setIsLoading(false);
      }
    }
    fetchMovieDetails();
  }, [selectedId]);

  const {
    Title: title,
    Year: year,
    Poster: poster,
    Runtime: runtime,
    Genre: genre,
    imdbRating,
    Plot: plot,
    Released: released,
  } = movie;

  function handleAdd() {
    const newWatchedMovie = {
      imdbID: selectedId,
      title,
      year,
      poster, // Ensure poster is included here
      imdbRating: Number(imdbRating),
      runtime: Number(runtime?.split(" ")[0]) || 0,
    };

    onAddWatched(newWatchedMovie);
    onCloseMovie();
  }

  return isLoading ? (
    <Loader />
  ) : (
    <div className="movie-details">
      <button
        style={{
          backgroundColor: "#6c63ff",
          color: "white",
          border: "none",
          borderRadius: "4px",
          padding: "10px 15px",
          fontSize: "1rem",
          cursor: "pointer",
          marginBottom: "1rem",
          boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
          transition: "transform 0.2s ease, background-color 0.2s ease",
        }}
        onMouseEnter={(e) => (e.target.style.backgroundColor = "#5348c7")}
        onMouseLeave={(e) => (e.target.style.backgroundColor = "#6c63ff")}
        onClick={onCloseMovie}
      >
        &larr; Back
      </button>
      <img src={poster} alt={`Poster of ${title}`} />
      <h2>{title}</h2>
      <p>
        <strong>Release Date:</strong> {released}
      </p>
      <p>
        <strong>Duration:</strong> {runtime}
      </p>
      <p>
        <strong>Genre:</strong> {genre}
      </p>
      <p>
        <strong>IMDb Rating:</strong> {imdbRating}
      </p>
      <p>{plot}</p>
      <button
        style={{
          backgroundColor: "#6c63ff",
          color: "white",
          border: "none",
          borderRadius: "4px",
          padding: "10px 15px",
          fontSize: "1rem",
          cursor: "pointer",
          marginTop: "1rem",
          boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
        }}
        onClick={handleAdd}
      >
        + Add to Watched
      </button>
    </div>
  );
}

function WatchedSummary({ watched }) {
  const totalMovies = watched.length;
  const avgImdbRating =
    watched.reduce((acc, movie) => acc + movie.imdbRating, 0) / totalMovies || 0;
  const avgRuntime =
    watched.reduce((acc, movie) => acc + movie.runtime, 0) / totalMovies || 0;

  return (
    <div className="summary">
      <h2>Watched Summary</h2>
      <p>Total Movies: {totalMovies}</p>
      <p>Average IMDb Rating: {avgImdbRating.toFixed(1)}</p>
      <p>Average Runtime: {avgRuntime.toFixed(0)} mins</p>
    </div>
  );
}

