import { useEffect, useRef, useState } from "react";
import StarRating from "./StarRating";
import { useKey } from "./useKey";
import { useLocalStorageState } from "./useLocalStorageState";
import { useMovies } from "./useMovies";

const KEY = "c388b899";

export default function App() {
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedId] = null;
  const { movies, isLoading, error } = useMovies(query);
  const [watched, setWatched] = useLocalStorageState([], "watched");

  function handleSelectMovie(id) {
    setSelectedId((current) => (current === id ? null : id)); // Toggle selection
  }

  function handleCloseMovie() {
    setSelectedId(null); // Close movie details view
  }

  function handleAddWatched(movie) {
    setWatched((currentWatched) => [...currentWatched, movie]); // Add movie to watched list
  }

  function handleDeleteWatched(id) {
    setWatched((currentWatched) =>
      currentWatched.filter((movie) => movie.imdbID !== id) // Remove movie from watched list
    );
  }

  return (
    <>
      <NavBar>
        <Logo />
        <Search query={query} setQuery={setQuery} />
        <NumResults movies={movies} />
      </NavBar>

      <Main>
        {/* Box 1: Search Results */}
        <Box title="Search Results">
          {isLoading && <Loader />}
          {!isLoading && !error && (
            <MovieList movies={movies} onSelectMovie={handleSelectMovie} />
          )}
          {error && <ErrorMessage message={error} />}
        </Box>

        {/* Box 2: Watched Movies */}
        <Box title="Watched Movies">
          {selectedId ? (
            <MovieDetails
              selectedId={selectedId}
              onCloseMovie={handleCloseMovie}
              onAddWatched={handleAddWatched}
              watched={watched}
            />
          ) : (
            <>
              <WatchedSummary watched={watched} />
              <WatchedMoviesList
                watched={watched}
                onDeleteWatched={handleDeleteWatched}
              />
            </>
          )}
        </Box>
      </Main>
    </>
  );
}

function Loader() {
  return <div className="loader">Loading...</div>; // Loading indicator
}

function ErrorMessage({ message }) {
  return <div className="error">{message}</div>; // Display error message
}

function NavBar({ children }) {
  return (
    <nav className="nav-bar">
      {children}
    </nav>
  );
}

function Logo() {
  return (
    <div className="logo">
      <span role="img" aria-label="popcorn">
        🍿
      </span>
      <h1>usePopcorn</h1>
    </div>
  );
}

function NumResults({ movies }) {
  return (
    <p className="num-results">
      Found <strong>{movies?.length || 0}</strong> results
    </p>
  );
}

function Box({ title, children }) {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="box">
      <div className="box-header">
        <h2>{title}</h2>
        <button className="btn-toggle" onClick={() => setIsOpen((open) => !open)}>
          {isOpen ? "–" : "+"}
        </button>
      </div>
      {isOpen && children}
    </div>
  );
}

function Movie({ movie, onSelectMovie }) {
  return (
    <li className="movie-item" onClick={() => onSelectMovie(movie.imdbID)}>
      <img src={movie.Poster} alt={`${movie.Title} poster`} />
      <h3>{movie.Title}</h3>
      <p>
        {movie.Year} • {movie.Type}
      </p>
    </li>
  );
}

function Search({ query, setQuery }) {
  const inputEl = useRef(null);

  useKey("Enter", function () {
    if (document.activeElement === inputEl.current) return;
    inputEl.current.focus();
    setQuery("");
  });

  return (
    <input
      className="search"
      type="text"
      placeholder="Search movies..."
      value={query}
      onChange={(e) => setQuery(e.target.value)}
      ref={inputEl}
    />
  );
}

function Main({ children }) {
  return <main className="main">{children}</main>;
}

function MovieList({ movies, onSelectMovie }) {
  return (
    <ul className="list list-movies">
      {movies?.map((movie) => (
        <Movie movie={movie} key={movie.imdbID} onSelectMovie={onSelectMovie} />
      ))}
    </ul>
  );
}

function MovieDetails({ selectedId, onCloseMovie, onAddWatched, watched }) {
  return <div>Movie Details Component</div>;
}

function WatchedSummary({ watched }) {
  return <div>Watched Summary Component</div>;
}

function WatchedMoviesList({ watched, onDeleteWatched }) {
  return <div>Watched Movies List Component</div>;
}
